    debug(N, K)
    #import pdb; pdb.set_trace()
    while 1:
        if len(N) == 0:
            return wins
        N,n = N[:-1], N[-1]
        if n > K[-1]:
            wins += 1
            K = K[1:]
            continue
        for idx, v in enumerate(K):
            if v > n:
                break
        K = K[:idx] + K[idx+1:]

def dwar_wins(N, K):
