def recycleables(N, A, B):
    t = set()
    s = str(N)
    for i in range(len(s)):
        s1 = int(s[i:] + s[:i])
        if A <= s1 <= B:
            t.add(s1)
    return t

def do_trial(A, B):
    total = 0
    seen = set()
    for i in xrange(A, B+1):
        if i not in seen:
            t = recycleables(i, A, B)
            z = len(t)
            total += z * (z-1) / 2
            seen.update(t)
    return total

