def rotate(i, ndigits):
    s = str(i)
    s = s[-ndigits:] + s[:-ndigits]
    return int(s)

def pairs(A, B):
    for n in range(A, B + 1):
        for i in range(1, len(str(n))):
            m = rotate(n, i)
            if n >= m or m > B: continue
            yield n, m

