    y = 0
    i = 0
    for j in range(N):
        while i < N and MN[i] < MK[j]:
            i += 1
        if i < N:
            y += 1
            i += 1
    z = N
    j = 0
    for i in range(N):
        while j < N and MN[i] > MK[j]:
            j += 1
        if j < N:
            z -= 1
            j += 1
    return (y, z)

