def isRecycled(n,m):
	if len(n) < 2:
		return 0

	for c in m:
		if c not in n:
			return 0

	for i in range (1, len(n)):
		mi = m[i:] + m[-len(m):-(len(m)-i)]
		if n == mi:
			return 1

	return 0


