cachetotals = 0
cachemisses = 0

def statreset():
    global cachemisses, cachetotals
    cachemisses = 0
    cachetotals = 0

class memoizeit(object):
    """Decorator. Caches a function's return value each time it is called.

    If called later with the same arguments, the cached value is returned 
    (not reevaluated).
    """

    def __init__(self, func):
        self.func = func
        self.cache = {}

    def __call__(self, *args):

        # update stats
        global cachetotals, cachemisses
        cachetotals += 1

        try:
            return self.cache[args]
        except KeyError:

            # update stats
            cachemisses += 1

            value = self.func(*args)
            self.cache[args] = value
            return value
        except TypeError:

            # update stats
            cachemisses += 1

            # uncachable -- for instance, passing a list as an argument.
            # Better to not cache than to blow up entirely.
            return self.func(*args)

    @property
    def __name__(self):
        return self.func.__name__

    def __get__(self, obj, objtype):
        """Support instance methods."""
        return functools.partial(self.__call__, obj)

class timeit(object):
    """Decorator that times a function.

    When function ends, print name, runtime, return value and cache stats.
    """

    def __init__(self, func):
        self.func = func

    def __call__(self, *args):
        start = time.time()
        value = self.func(*args)
        delta = time.time() - start
        cachedata = (1 - cachemisses/(cachetotals * 1.0)) if \
            cachetotals else 0
        print (self.func.__name__, "{:7.3f}s, (res: {}, cache: {:.2%})".format(
            delta, value, cachedata))
        return value

    def __get__(self, obj, objtype):
        return functools.partial(self.__call__, obj)

