 def isFair(x):
 	l1 = list(str(x))
 	l2 = list(l1)
 	l2.reverse()
 	return l1 == l2

 def solve(a, b):
 	c = 0
 	for i in xrange(a, b + 1):
 		r = int(sqrt(i))
 		if r * r == i and isFair(i) and isFair(r):
 			c += 1

