     m = len(g[0])
     queue = [(0, 0)]

     while queue:
         v = queue.pop(0)
         g[v[0]][v[1]] = 'r'
         bomb = False
         for (i, j) in neighbours(g, v, n, m):
             if g[i][j] == '*':
                 bomb = True
         if not bomb:
             for (i, j) in neighbours(g, v, n, m):
                 if g[i][j] != 'r':
                     #print('append')
                     queue.append((i, j))

     for i in range(n):
