         grid[k-1][j] = '*'
     k -= 1
     M -= l
     return (grid, k, l, M)

 def reduceCols(grid, k, l, M):
     if l <= 2:
         return (grid, k, l, M)

     #num_cols = M / k
     #lowest = max(l - num_cols, 2)
     #num_cols = l - lowest

     for i in range(k):
         grid[i][l-1] = '*'
     l -= 1
     M -= k
     return (grid, k, l, M)

 def solve(R, C, M):
