 import sys
 from math import ceil

 def solve(n, s, p, scores):
     count = 0
     for t in scores:
         if ceil(t/3) >= p:
             # This one must have a best score, even if it wasn't surprising.
             count += 1
         elif 2 <= t <= 28 and ceil((t+2)/3) >= p and s > 0:
             # This one could be surprising
             count += 1
             s -= 1
     return count

