 	data = line.strip().split(' ')
 	maxsurprising = int(data[1])
 	p = int(data[2])
 	scores = data[3:]
 	maxgooglers = 0

 	for s in scores:
 		(best, bestsurp) = findbest(int(s))
 		if best >= p:
 			maxgooglers += 1
 		else:
 			if bestsurp >= p and maxsurprising > 0:
 				maxgooglers += 1
 				maxsurprising -= 1

 	print("Case #%d: %d" % (casenum, maxgooglers))

