 def get_max( x, scores, score_needed, num_surprises ):
 	scores = sorted( scores, reverse=True )
 	numPass = 0
 	i = 0;
 	while ( i < len(scores) ):
 		if ( x[scores[i]][0] >= score_needed ):
 			numPass += 1
 		else:
 			break
 		i += 1

 	while ( i < len(scores) and num_surprises > 0 ):
 		if ( x[scores[i]][1] >= score_needed ):
 			numPass += 1
 			num_surprises -= 1

 		i += 1

 	return numPass

