 				assert len(line.strip().split(' ')) == 2
 				N = int(line.strip().split(' ')[0])
 				M = int(line.strip().split(' ')[1])
 				lines_left = N
 				case.append(N)
 				case.append(M)
 				if not lines_left:
 					new_case = True
 					yield case_counter, case
 				continue
 			if lines_left:
 				lines_left -= 1
 				case.append(line.strip().split(' '))
 			if not lines_left:
 				new_case = True
 				yield case_counter, case

