def checkRow(r):
 	c = r[0];

 	# making sure it isnt an empty line
 	if (c == '.'):
 		return (False, None)

 	for i in range(1,4):
 		if not (r[i] == c or r[i] == 'T'):
 			return (False, None)

 	# if it got this far it must be right
 	return (True, c)

 def checkCol(L, col):
 	c = L[0][col];

 	# making sure it isnt an empty line
 	if (c == '.'):
 		return (False, None)

 	for i in range(1,4):
 		if not (L[i][col] == c or L[i][col] == 'T'):
 			return (False, None)

 	# if it got this far it must be right
 	return (True, c)

 def checkDiag(L):
 	c = L[0][0];

 	for i in range(1,4):
 		if not (L[i][i] == c or L[i][i] == 'T'):
 			break
 	else:
 		if (c != '.'):
 			# if it got this far it must be right
 			return (True, c)

 	#first diag failed
 	c = L[0][3];

 	# making sure it isnt an empty line
 	if (c == '.'):
 		return (False, None)

 	for i in range(1,4):
 		if not (L[i][3-i] == c or L[i][3-i] == 'T'):
 			break
 	else:
 		# if it got this far it must be right
 		return (True, c)

 	return (False, None)

 def checkComplete(L):
 	for i in range(4):
 		if '.' in L[i]:
 			return False
 	return True

