"""
 Google Code Jam 2014 Qualification Problem C
 Usage:
     python c.py < input.txt > output.txt
 """
 import copy
 import itertools
 import sys


 def iter_neighbors(x, y, cells):
     columns = len(cells[0])
     rows = len(cells)

     if y > 0:
         if x > 0:
             yield x - 1, y - 1
         yield x, y - 1

         if x + 1 < columns:
             yield x + 1, y - 1

     if x > 0:
         yield x - 1, y

     if x + 1 < columns:
         yield x + 1, y

     if y + 1 < rows:
         if x > 0:
             yield x - 1, y + 1

         yield x, y + 1

         if x + 1 < columns:
             yield x + 1, y + 1


 def try_to_click(x, y, cells, remaining):
     if remaining == 0:
         return cells

     recent = []

     opened = 0

     for n_x, n_y in iter_neighbors(x, y, cells):
         if cells[n_y][n_x] == "?":
             cells[n_y][n_x] = "."
             opened += 1
             recent.append((n_x, n_y))

     if opened == remaining:
