 def is_palindrome(N):
     s = str(N)
     return s == ''.join(reversed(s))

 def fair_and_square_set(max_N):
     p = "squareset_%d" % max_N
     try:
         s = pickle.load(file(p))
         return s
     except:
         pass
     s = set()
     for i in range(1,max_N+1):
         if is_palindrome(i) and is_palindrome(i*i):
             s.add(i*i)
     pickle.dump(s, file(p, "wb"))
     return s

