 import sys,os
 import numpy as np

 BIG_NUM=100000
 def Solve(C,F,X):
     best_time=float('inf')
     for n in xrange(0,BIG_NUM):
         if n==0:
             farm_time=0
         else:
             farm_time += C/(2+(n-1)*F)
         if best_time<=farm_time: break
         cookie_time=X/(2+n*F)

         if farm_time+cookie_time<best_time:
             best_time=farm_time+cookie_time

     return '%.9f'%best_time



