 def is_palindrome(n):
     n = str(n)
     return all(n[i] == n[len(n)-1-i] for i in range(len(n) // 2))

 def mysqrt(n):
     """Return -1 if not an integer"""
     rt = int(sqrt(n))
     return rt if rt * rt == n else -1


