import sys

 def all_grids(R,C,M, lp=0,placed=0):
     rem = R*C-lp
     if M-placed >= rem:
         if M-placed > rem:
             return None

         res = set()
         for i in range(lp, R*C):
             res.add(i)
         return [res]

     if placed == M:
         return [set()]

     res = []
     for i in range(lp, R*C):
         sub_sol = all_grids(R,C,M,i+1, placed+1)
         if sub_sol is None:
             continue

         for s in sub_sol:
             s.add(i)
             res.append(s)
     return res

 def adj(R,C,i):

     res = []
     left_edge = i % C == 0
     top_edge = i // C == 0
     right_edge = (i+1) % C == 0
     bottom_edge = i // C == R-1

     if not left_edge:
         res.append(i-1)
         if not top_edge:
             res.append(i-1-C)
         if not bottom_edge:
             res.append(i+C-1)

     if not right_edge:
         res.append(i+1)
         if not top_edge:
             res.append(i+1-C)
         if not bottom_edge:
             res.append(i+1+C)

     if not bottom_edge:
         res.append(i+C)
     if not top_edge:
         res.append(i-C)
     return res




 def solution(R,C,g):
     M = R*C
     res = []
     num_zeroes = 0
     for i in range(M):
         if i in g:
             res.append('x')
             continue
         x = 0
         for a in adj(R,C,i):
             if a in g:
                 x += 1
         if x == 0:
