 def aux(A, B, x, s):
     res = 0
     n = int(''.join(x[::-1]+x))
     nn = n*n
     if nn > B:
         return res
     if nn >= A:
         res += 1
     for c in [0, 1, 2]:
         if s + c*c < 10:
             n = int(''.join(x[::-1]+[str(c)]+x))
             nn = n*n
             if nn >= A and nn <= B:
                 res += 1
         if s + 2*c*c < 10:
             res += aux(A, B, [str(c)]+x, s + 2*c*c)
     return res

