 def markMax(max_,start,end,marked,isRow,lawn):
     for i in range(end):
             if(isRow):
                 if( lawn[start][i]==max_):
                     marked[start][i]=-1
             else: 
                 if( lawn[i][start]==max_):
                     marked[i][start]=-1




 def mowerLawn(lawn,m,n,marked):
     #marked= [[0] *n]*m
     for i in range(m):
         max_=max(lawn[i])
         markMax(max_,i,n,marked,True,lawn) 

     for i in range(n):
         colList=[]
         for j in range(m):
             colList.append(lawn[j][i])
         max_=max(colList)
         markMax(max_,i,m,marked,False,lawn) 

     done=True
     for i in range(m):
         for j in range(n):
             if(marked[i][j]!=-1):
                 done=False
                 break
         if(not done):
             break
     if(done):
         return 'YES'
     else:
         return 'NO'




