     return s

 def z_and_nei(z, r, c):
     s = set()
     for x in z:
         s.add(x)
         s.add((x[0]-1,x[1]-1))
         s.add((x[0]-1,x[1]))
         s.add((x[0]-1,x[1]+1))
         s.add((x[0],x[1]-1))
         s.add((x[0],x[1]+1))
         s.add((x[0]+1,x[1]-1))
         s.add((x[0]+1,x[1]))
         s.add((x[0]+1,x[1]+1))
     o = set()
     for x in s:
         if x[0] < 0 or x[0] > r-1 or x[1] < 0 or x[1] > c-1:
             o.add(x)
     s-=o
     return s

 def find_config(z, r, c, t):
     if count(z,r,c) == t:
         return z
     if count(z,r,c) > t:
         return []
     n = nei(z,r,c)
     for x in n:
         z.append(x)
         if find_config(z,r,c,t) != []:
             return z
         z.pop()
     return []

