 import sys


 def process(C, F, X):
 	speed = 2.0
 	time = 0.0
 	while True:
 		if C / speed + X / (speed + F) > X / speed:
 			time += X / speed
 			break
 		time += C / speed
 		speed += F
 	return round(time, 7)

